import React from 'react'
import Header from '../header'
import { Link } from 'react-router-dom'
class List extends React.Component{
  constructor(props){
    super(props);
    this.listPokemons = this.listPokemons.bind(this)
    this.getTotalPages = this.getTotalPages.bind(this)

    this.state = {
      pokemonList: this.props.pokemonList,
      listedPokemons: [],
      pages: 0,
    }
  }
  componentDidMount(){
    async function list(){

      await this.listPokemons()
      this.setState({pages: this.getTotalPages()}, ()=> console.log(this.state.pages))
    }
    list()
  }

  getTotalPages(){
    const {pokemonList} = this.state;
    const maxPokemonsPerPage = 10
    console.log(pokemonList.length)
    return Math.ceil(pokemonList.length / maxPokemonsPerPage) 
  }

  listPokemons(){
    const {pokemonList} = this.state;
    const listed = pokemonList.reduce((acc, currentPokemon)=>{
      const length = acc.length;
      const listLimit = 10
      length < listLimit && acc.push(currentPokemon)
      return acc
    }, [])

    this.setState({listedPokemons:listed})
  }

  render(){
    return(
      <div>
        <p>List</p>
      </div>

    )
  }
}

export default List;
