import React from 'react'
import Header from '../header'
import { Link } from 'react-router-dom'
class List extends React.Component{
  constructor(props){
    super(props);
    this.listPokemons = this.listPokemons.bind(this)
    this.getTotalPages = this.getTotalPages.bind(this)

    this.state = {
      pokemonList: this.props.pokemonList,
      listedPokemons: [],
      pages: 0,
    }
  }
  componentDidMount(){
    this.setState({pages: this.getTotalPages()})
  }

  getTotalPages(){
    const {pokemonList} = this.state;
    const totalPokemonsPerPage = 10
    return Math.ceil(pokemonList.length % totalPokemonsPerPage) 
  }

  listPokemons(){
    const {pokemonList} = this.state;
    const listed = pokemonList.reduce((acc, currentPokemon)=>{
      const length = acc.length;
      const listLimit = 10
      length < listLimit && acc.push(currentPokemon)
      return acc
    }, [])
    console.log(listed)
  }

  render(){
    return(
      <div>

    <p>List{this.listPokemons()}</p>
    {
      console.log(this.state.pages)
    }
      </div>

    )
  }
}

export default List;
