import React from 'react'

class Home extends React.Component {
  constructor(){
    super()
    this.state ={
      pokemonList: []
    }
    this.fetchPokemons = this.fetchPokemons.bind(this)
  }

  async fetchPokemons(){
    const endpoint = 'https://pokeapi.co/api/v2/pokemon?limit=151'
    const {results} =  await fetch(endpoint).then(res => res.json() )
    this.setState(()=>({ pokemonList:results }),()=> console.log(this.state))
  }

  componentDidMount(){
    this.fetchPokemons()
  }

  render(){

    const {pokemonList} = this.state;

    return(
      <div>
        <ul>
         {pokemonList.map((pokemon)=> <li key={pokemon.name}>{pokemon.name}</li> )}
        </ul>
      </div>
    )
  }
}
export default Home
